(function(){var initializeBlock=function(block,attr){const el=attr?block[0]:block;if('function'!=typeof jQuery){return false;}
if(!attr){jQuery(el.querySelector('.slickjs__track')).slick({autoplay:true,autoplaySpeed:5000,arrows:false,cssEase:'cubic-bezier(0.39, 0.575, 0.565, 1)',dots:true,appendDots:el.querySelector('.slickjs__bullets'),slidesToShow:1,speed:550,draggable:false,fade:true});}else{if(el){jQuery(el.querySelector('.slickjs__track')).slick({arrows:false,dots:false,slidesToShow:1,draggable:false});}}};document.addEventListener('DOMContentLoaded',function(){[...document.querySelectorAll('.wp-block-quote-carousel')].forEach((slickjs)=>initializeBlock(slickjs));});if(window.acf){window.acf.addAction('render_block_preview/type=quotes',initializeBlock);}}());