(function(){var initializeBlock=function(block,attr){const el=attr?block[0]:block;if('function'!=typeof jQuery){return false;}
if(!attr){jQuery(el.querySelector('.slickjs__track')).slick({autoplay:true,autoplaySpeed:8500,arrows:false,cssEase:'cubic-bezier(0.39, 0.575, 0.565, 1)',dots:true,appendDots:el.querySelector('.slickjs__dots > .wrap'),slidesToShow:1,speed:550});jQuery(el.querySelector('.slickjs__track ')).on('beforeChange',(event,slick,currentSlide,nextSlide)=>{const slideEl=jQuery(slick.$slides[nextSlide]);jQuery(slideEl).find('p').css({transition:'none',opacity:0,transform:'translateX(-1rem)'});});jQuery(el.querySelector('.slickjs__track ')).on('afterChange',(event,slick,currentSlide)=>{const slideEl=jQuery(slick.$slides[currentSlide]);jQuery(slideEl).find('p').css({transition:'all 550ms cubic-bezier(0.39, 0.575, 0.565, 1)',opacity:1,transform:'translateX(0)'});});}else{if(el){jQuery(el.querySelector('.slickjs__track')).slick({arrows:false,dots:false,slidesToShow:1,draggable:false});}}};document.addEventListener('DOMContentLoaded',function(){[...document.querySelectorAll('.wp-block-acf-masthead-rotator')].forEach((glide)=>initializeBlock(glide));});if(window.acf){window.acf.addAction('render_block_preview/type=masthead-rotator',initializeBlock);}}());